void partida_otra(bool &otra_partida) 
{
    int** escenario = crearMatriz(45, 200);
    for (int i = 0; i < 45; i++) 
    {
        for (int j = 0; j < 200; j++) 
        {
            escenario[i][j] = matriz_otra_partida[i][j];
        }
    }
    color_escenarios(escenario, 45, 200, 0, 0);
    char k;
    do {
        k = _getch();
        if (k - '0' == 1) { otra_partida = true; }
        if (k - '0' == 2) { otra_partida = false; }
    } while (k - '0' != 1 && k - '0' != 2);
    for (int i = 0; i < 45; i++) { delete[] escenario[i]; }
    delete[] escenario;
}


struct ranking
{
    string nombre;
    int puntj;
};
ranking* agregar_nuevo_usuario(ranking* ranking_partida, int& tam_rank, int puntj, string nombre)
{
    ranking* temporal = new ranking[tam_rank + 1];
    for (int i = 0; i < tam_rank; i++)
    {
        temporal[i] = ranking_partida[i];
    }
    temporal[tam_rank].nombre = nombre;
    temporal[tam_rank].puntj = puntj;
    tam_rank++;
    delete[] ranking_partida;
    return temporal;
}
ranking* ordenar_ranking(ranking* ranking_partida, int tam_rank)
{
    ranking temporal;
    for (int i = 0; i < tam_rank; i++)
    {
        for (int k = i + 1; k < tam_rank; k++)
        {
            if (ranking_partida[i].puntj < ranking_partida[k].puntj)
            {
                temporal = ranking_partida[i];
                ranking_partida[i] = ranking_partida[k];
                ranking_partida[k] = temporal;
            }
        }
    }
    return ranking_partida;
}
