int* agregar_letra(int* name, int& size, char k) {
    int* tempo = new int[size + 1];
    for (int i = 0; i < size; i++) {
        tempo[i] = name[i];
    }
    tempo[size] = int(k);
    size++;
    delete[] name;
    return tempo;
}

int* quitar_letra(int* name, int& size) {
    if (size == 0) {
        return nullptr;
    }
    int* tempo = new int[size - 1];
    for (int i = 0; i < size - 1; i++) {
        tempo[i] = name[i];
    }
    size--;
    delete[] name;
    return tempo;
}
