void print_comentario(char** matriz, int height, int width, int x, int y) {
    Console::ForegroundColor = ConsoleColor::Black;
    int segundos;
    bool saltar_texto = 0;
    for (int f = 0; f < height; f++)
    {
        cursor(x, y + f);
        for (int c = 0; c < width; c++)
        {
            if (_kbhit()) {
                char k = _getch();
                if (k == 13)saltar_texto = 1;
            }
            if (matriz[f][c] == ' ')cursor(x + c + 1, y + f);
            else {
                cout << matriz[f][c];
                segundos = 20;
                segundos += ((matriz[f][c] == '.') * 500) + ((matriz[f][c] == ',' || matriz[f][c] == ';') * 300);
                if (!saltar_texto)_sleep(segundos);
            }
        }
        cout << endl;
    }
}
